document.addEventListener('DOMContentLoaded', function() {
    // Sample data for favorite products
    const sampleFavorites = [
      {
        id: 1,
        name: "Ração Canina",
        price: 49.99,
        image: "data:image/jpeg;base64,",
        category: "Comida",
        rating: 4.8
      },
      {
        id: 2,
        name: "Brinquedos Interativos",
        price: 24.99,
        image: "https://images-americanas.b2w.io/produtos/7468118665/imagens/brinquedo-de-arremesso-de-cachorro-squeaky-dog-ball-brinquedos-interativos-coloridos-para-caes-bolas-de-borracha-para-caes-brinquedos-interativos-para-caes-com-sino-bola-de-brinquedo-para-mastigar-para-limpeza-de-dentes/7468118665_1_xlarge.jpg",
        category: "Brinquedos",
        rating: 4.5
      },
      {
        id: 3,
        name: "Cama Luxuosa",
        price: 89.99,
        image: "https://i.pinimg.com/474x/5c/13/aa/5c13aadb532522633b0d457c8f6cda77.jpg",
        category: "Acessórios",
        rating: 4.9
      },
      {
        id: 4,
        name: "Guloseimas naturais para gatos",
        price: 15.99,
        image: "data:image/jpeg;base64,",
        category: "Comida",
        rating: 4.7
      },
      {
        id: 5,
        name: "Trela ​​de treinamento para cães",
        price: 35.99,
        image: "data:image/jpeg;base64,",
        category: "Acessórios",
        rating: 4.6
      },
      {
        id: 6,
        name: "Conjunto de bola",
        price: 12.99,
        image: "data:image/jpeg;base64,",
        category: "Brinquedos",
        rating: 4.3
      }
    ];
  
    // DOM elements
    const favoritesList = document.getElementById('favorites-list');
    const favoritesCount = document.getElementById('favorites-total');
    const searchInput = document.getElementById('favorites-search');
    const filterButtons = document.querySelectorAll('.favorites-filter-button');
    const clearButton = document.getElementById('favorites-clear');
    
    let currentFavorites = [...sampleFavorites];
    let activeFilter = 'All';
  
    // Initialize the page
    renderFavorites();
    updateFavoritesCount();
  
    // Render all favorite items
    function renderFavorites() {
      // Clear the current list
      favoritesList.innerHTML = '';
      
      // If no favorites, show empty state
      if (currentFavorites.length === 0) {
        renderEmptyState();
        return;
      }
  
      // Render each favorite item
      currentFavorites.forEach((item, index) => {
        const delay = index * 0.1;
        const favoriteItem = createFavoriteItem(item, delay);
        favoritesList.appendChild(favoriteItem);
      });
    }
  
    // Create a single favorite item element
    function createFavoriteItem(item, delay) {
      const itemElement = document.createElement('div');
      itemElement.id = `favorites-item-${item.id}`;
      itemElement.className = 'favorites-item fade-in';
      itemElement.style.animationDelay = `${delay}s`;
      
      // Generate star rating HTML
      const ratingHtml = generateRatingStars(item.rating);
      
      itemElement.innerHTML = `
        <div id="favorites-item-badge-${item.id}" class="favorites-item-badge">${item.category}</div>
        <img id="favorites-item-image-${item.id}" class="favorites-item-image" src="${item.image}" alt="${item.name}">
        <div id="favorites-item-content-${item.id}" class="favorites-item-content">
          <h3 id="favorites-item-title-${item.id}" class="favorites-item-title">${item.name}</h3>
          <div id="favorites-item-rating-${item.id}" class="favorites-rating">
            ${ratingHtml}
          </div>
          <p id="favorites-item-price-${item.id}" class="favorites-item-price">$${item.price.toFixed(2)}</p>
          <div id="favorites-item-actions-${item.id}" class="favorites-item-actions">
            <button id="favorites-add-cart-${item.id}" class="favorites-item-button favorites-add-cart" data-id="${item.id}">
              Adicionar ao Carrinho
            </button>
            <button id="favorites-remove-${item.id}" class="favorites-item-button favorites-remove" data-id="${item.id}">
              <span id="favorites-remove-icon-${item.id}">✕</span>
            </button>
          </div>
        </div>
      `;
      
      // Add event listeners
      setTimeout(() => {
        const addToCartBtn = itemElement.querySelector(`.favorites-add-cart`);
        const removeBtn = itemElement.querySelector(`.favorites-remove`);
        
        addToCartBtn.addEventListener('click', () => {
          addToCart(item.id);
        });
        
        removeBtn.addEventListener('click', () => {
          removeFromFavorites(item.id);
        });
      }, 0);
      
      return itemElement;
    }
  
    // Generate star rating HTML
    function generateRatingStars(rating) {
      let starsHtml = '';
      const fullStars = Math.floor(rating);
      const hasHalfStar = rating % 1 >= 0.5;
      
      for (let i = 1; i <= 5; i++) {
        if (i <= fullStars) {
          starsHtml += `<span id="favorites-star-${i}" class="favorites-star">★</span>`;
        } else if (i === fullStars + 1 && hasHalfStar) {
          starsHtml += `<span id="favorites-star-${i}" class="favorites-star">★</span>`;
        } else {
          starsHtml += `<span id="favorites-star-${i}" class="favorites-star favorites-star-empty">★</span>`;
        }
      }
      
      return starsHtml;
    }
  
    // Render empty state
    function renderEmptyState() {
      const emptyState = document.createElement('div');
      emptyState.id = 'favorites-empty';
      emptyState.className = 'favorites-empty';
      
      emptyState.innerHTML = `
        <div id="favorites-empty-icon" class="favorites-empty-icon">💔</div>
        <h2 id="favorites-empty-text" class="favorites-empty-text">Nao há favoritos </h2>
        <button id="favorites-empty-button" class="favorites-empty-button">Explorar produtos</button>
      `;
      
      favoritesList.appendChild(emptyState);
      
      // Add event listener to the button
      setTimeout(() => {
        const exploreBtn = document.getElementById('favorites-empty-button');
        exploreBtn.addEventListener('click', () => {
          // Redirect to products page (replace with actual URL)
          console.log('Redirecting to products page');
          // window.location.href = '/products';
        });
      }, 0);
    }
  
    // Update favorites count
    function updateFavoritesCount() {
      favoritesCount.textContent = currentFavorites.length;
    }
  
    // Remove item from favorites
    function removeFromFavorites(id) {
      // Find the item to remove
      const itemElement = document.getElementById(`favorites-item-${id}`);
      
      // Add animation class
      itemElement.style.opacity = '0';
      itemElement.style.transform = 'scale(0.8)';
      itemElement.style.transition = 'all 0.3s ease';
      
      // Remove item after animation completes
      setTimeout(() => {
        currentFavorites = currentFavorites.filter(item => item.id !== id);
        renderFavorites();
        updateFavoritesCount();
        
        // Show toast notification
        showToast(`Item removido dos favoritos`);
      }, 300);
    }
  
    // Add item to cart
    function addToCart(id) {
      // Find the item
      const item = currentFavorites.find(item => item.id === id);
      
      // Here you would typically add the item to the cart
      // For this demo, we'll just show a notification
      showToast(`${item.name} adicionado ao carrinho`);
      
      // Add animation to the button
      const button = document.getElementById(`favorites-add-cart-${id}`);
      button.innerHTML = 'Adicionado ✓';
      button.style.backgroundColor = '#4caf50';
      
      // Reset the button after a delay
      setTimeout(() => {
        button.innerHTML = 'Adicionar ao carrinho';
        button.style.backgroundColor = '';
      }, 2000);
    }
  
    // Show toast notification
    function showToast(message) {
      // Create toast element
      const toast = document.createElement('div');
      toast.id = 'favorites-toast';
      toast.className = 'favorites-toast';
      toast.textContent = message;
      
      // Add the toast to the document
      document.body.appendChild(toast);
      
      // Trigger animation
      setTimeout(() => {
        toast.classList.add('favorites-toast-visible');
      }, 10);
      
      // Remove the toast after a delay
      setTimeout(() => {
        toast.classList.remove('favorites-toast-visible');
        setTimeout(() => {
          document.body.removeChild(toast);
        }, 300);
      }, 3000);
    }
  
    // Filter favorites by category
    function filterFavorites(category) {
      if (category === 'All') {
        currentFavorites = [...sampleFavorites];
      } else {
        currentFavorites = sampleFavorites.filter(item => item.category === category);
      }
      
      renderFavorites();
    }
  
    // Search favorites
    function searchFavorites(term) {
      const searchTerm = term.toLowerCase();
      
      if (searchTerm === '') {
        filterFavorites(activeFilter); // Reset to current filter
        return;
      }
      
      // Filter items based on the search term
      let filtered = sampleFavorites;
      
      // Apply category filter if active
      if (activeFilter !== 'All') {
        filtered = filtered.filter(item => item.category === activeFilter);
      }
      
      // Then apply search filter
      currentFavorites = filtered.filter(item => 
        item.name.toLowerCase().includes(searchTerm) ||
        item.category.toLowerCase().includes(searchTerm)
      );
      
      renderFavorites();
    }
  
    // Event listeners
    searchInput.addEventListener('input', (e) => {
      searchFavorites(e.target.value);
    });
    
    clearButton.addEventListener('click', () => {
      if (currentFavorites.length === 0) return;
      
      // Confirm before clearing
      if (confirm('Tem certeza de que deseja remover todos os favoritos?')) {
        currentFavorites = [];
        renderFavorites();
        updateFavoritesCount();
        showToast('Todos os favoritos foram apagados');
      }
    });
    
    // Filter button events
    filterButtons.forEach(button => {
      button.addEventListener('click', () => {
        // Remove active class from all buttons
        filterButtons.forEach(btn => btn.classList.remove('favorites-active'));
        
        // Add active class to clicked button
        button.classList.add('favorites-active');
        
        // Get the category
        const category = button.textContent;
        activeFilter = category;
        
        // Filter the favorites
        filterFavorites(category);
      });
    });
  
    // Add toast styling dynamically
    const style = document.createElement('style');
    style.textContent = `
      .favorites-toast {
        position: fixed;
        bottom: 20px;
        right: 20px;
        background-color: var(--primary-color);
        color: white;
        padding: 12px 20px;
        border-radius: var(--border-radius);
        box-shadow: var(--box-shadow);
        z-index: 1000;
        opacity: 0;
        transform: translateY(20px);
        transition: all 0.3s ease;
        font-family: "Poppins";
      }
      
      .favorites-toast-visible {
        opacity: 1;
        transform: translateY(0);
      }
    `;
    document.head.appendChild(style);
  });